import "../../../../scss/utils/_all.scss";
import "../../../../scss/app/_controlPanel.scss";
import { NavLink as Link } from "react-router-dom";

import React, { Component } from "react";

export default class ControlPanel extends Component {

  constructor(props) {
    super(props)

    this.state = {
       clicked: false
    }
  }

  changeColor(){
    this.setState({clicked: !this.state.clicked})
 }

  render() {
    const active = {
      fontWeight: "700",
      borderLeft: "10px solid #E58A20",
      height:'70px',
      arrow: {
        color: "#E58A20",
      },
    };

   let btn_class = this.state.clicked ? "clicked" : "unclicked";


    return (
      <div className="controlPanelWrap col-lg-12">
        <div className="sidePanelWrap col-lg-2">
          <ul>
            <li 
            className={btn_class} 
            onClick={this.changeColor.bind(this)}
            >
              <Link 
              exact to="/mainApp/mainPanel" 
              >
             <span className="option">Pulpit </span>
             <span className="arrow">&#62;</span>
              </Link>
            </li>

            <li>
              <Link 
              exact to="/mainApp/recipes" 
              activeStyle={active}
              >
             <span className="option">Przepisy </span>
             <span className="arrow">&#62;</span>
              </Link>
            </li>
            <li>
              <Link 
              exact to="/mainApp/plans" 
              activeStyle={active}
              >
             <span className="option">Plany </span>
             <span className="arrow">&#62;</span>
              </Link>
            </li>


          </ul>
        </div>
        <div className="componentPanel col-lg-10"></div>
      </div>
    );
  }
}
